function deleteFile(file_code){
  $.ajax({
    url: "/delete-file",
    type: "POST",
    data: {file_code : file_code},
    success: function(response){
      console.log(`success ${response}`);
      location.reload();
    },
    error: function(xhr){
      console.log(`error ${xhr}`)
    }
  });
  $(function() {
    $(document).on('click', '#deleteButton', function() {
      location.reload();
    });
  });
}

